    "United Arab Emirates", "United Kingdom", "United States", "Uruguay", "Uzbekistan", "Venezuela",
    "Vietnam", "Yemen", "Zambia", "Zimbabwe"
  ];

  citiesByCountry: { [key: string]: string[] } = {
    "Afghanistan": ["Kabul", "Kandahar", "Herat", "Mazar-i-Sharif", "Jalalabad"],
    "Albania": ["Tirana", "Durrës", "Vlorë", "Shkodër", "Fier"],
    "Algeria": ["Alger", "Oran", "Constantine", "Annaba", "Blida"],
    "Andorra": ["Andorre-la-Vieille", "Escaldes-Engordany", "Encamp", "Encamp", "Sant Julià de Lòria"],
    "Angola": ["Luanda", "N'dalatando", "Huambo", "Lubango", "Benguela"],
    "Argentina": ["Buenos Aires", "Córdoba", "Rosario", "Mendoza", "La Plata"],
    "Armenia": ["Erevan", "Gyumri", "Vanadzor", "Vagharshapat", "Hrazdan"],
    "Australia": ["Sydney", "Melbourne", "Brisbane", "Perth", "Adélaïde"],
    "Austria": ["Vienne", "Graz", "Salzbourg", "Innsbruck", "Linz"],
