          // Gérer le cas où result n'est pas une chaîne
          console.error('Le type de résultat n\'est pas une chaîne.', result);
        }
      };

      reader.readAsDataURL(file); // Ou readAsText(file) selon vos besoins
    }
  }

  validateForm(): boolean {
    return (
      this.user.firstName !== '' &&
      this.user.lastName !== '' &&
      this.user.country !== '' &&
      this.user.city !== '' &&
      this.user.dob !== '' &&
      this.user.gender !== '' &&
      this.user.idCardFront !== null &&
      this.user.idCardBack !== null &&
      this.user.selfie !== null
    );
  }


takeSelfie(event: Event) {
  // Prévenir la soumission du formulaire
  event.preventDefault();

  // Sélectionner l'élément vidéo et canvas
  const video = document.getElementById('videoElement') as HTMLVideoElement;
  const canvas = document.querySelector('canvas#canvasElement') as HTMLCanvasElement;
  const selfiePreview = document.getElementById('selfiePreview') as HTMLImageElement;

  // Vérifier que l'élément vidéo est bien présent
  if (!video) {
    console.error('Erreur : L\'élément vidéo est introuvable.');
    return;
  }

  // Créer un contexte pour le canvas
  const context = canvas.getContext('2d');
  if (!context) {
    console.error('Erreur : Impossible d\'obtenir le contexte du canvas.');
    return;
  }

  // Dessiner l'image du flux vidéo dans le canvas
  canvas.width = video.videoWidth;
  canvas.height = video.videoHeight;
  context.drawImage(video, 0, 0, canvas.width, canvas.height);

  // Convertir le canvas en une image base64
  this.user.selfie = canvas.toDataURL('image/jpeg'); // Convertir en chaîne base64

  // Afficher l'aperçu de la selfie
  if (selfiePreview) {
    selfiePreview.src = this.user.selfie;
    selfiePreview.style.display = 'block'; // Afficher l'aperçu
  } else {
    console.error('Erreur : L\'élément d\'aperçu selfie est introuvable.');
  }

  // Arrêter le flux vidéo après la capture de la selfie
  const stream = video.srcObject as MediaStream;
  const tracks = stream?.getTracks();
  if (tracks) {
    tracks.forEach(track => track.stop()); // Arrêter tous les tracks (caméra)
  }

  // Masquer la vidéo
  video.style.display = 'none';
}


restartCamera() {
  if (this.videoElement?.nativeElement) {
    const stream = this.videoElement.nativeElement.srcObject as MediaStream;
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
    }
  }

  setTimeout(() => {
    this.startCamera();
  }, 500);
}




  onCountryChange() {
    const selectedCountry = this.user.country;
    this.cities = this.citiesByCountry[selectedCountry] || [];
    this.user.city = '';
  }

  onDateChange() {
    const birthdate = new Date(this.user.dob);
    const today = new Date();

    if (birthdate.getFullYear() > today.getFullYear()) {
      this.dateError = "Choix incorrect : l'année choisie ne peut pas être supérieure à l'année actuelle.";
      this.ageError = "";
      return;
    } else {
      this.dateError = "";
    }

    const age = today.getFullYear() - birthdate.getFullYear();
    const monthDiff = today.getMonth() - birthdate.getMonth();

    if (age < 18 || (age === 18 && monthDiff < 0)) {
      this.ageError = "Vous devez avoir au moins 18 ans.";
    } else {
      this.ageError = "";
    }
  }

  isSubmitting = false;

  startCamera() {
    const video = document.getElementById('videoElement') as HTMLVideoElement;

    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      navigator.mediaDevices.getUserMedia({ video: true })
        .then(stream => {
          video.srcObject = stream;
          video.style.display = "block"; // Rendre la vidéo visible
        })
        .catch(error => {
          console.error("Erreur d'accès à la caméra :", error);
        });
    }
  }

  stopCamera() {
    const video = document.getElementById('videoElement') as HTMLVideoElement;
    const stream = video.srcObject as MediaStream;

    if (stream) {
      const tracks = stream.getTracks();
      tracks.forEach(track => track.stop()); // Arrêter la caméra
      video.srcObject = null;
      video.style.display = "none"; // Cacher la vidéo
    }
  }

  onSubmit() {
    if (this.isSubmitting) {
      return;
    }

    this.isSubmitting = true;

    if (!this.user.selfie) {
      alert("Veuillez prendre une selfie avant de soumettre.");
      this.isSubmitting = false;
      return;
    }

    const birthdate = new Date(this.user.dob);
    const today = new Date();

    if (birthdate.getFullYear() > today.getFullYear()) {
      alert("Choix incorrect : l'année choisie ne peut pas être supérieure à l'année actuelle.");
      this.isSubmitting = false;
      return;
    }

    const age = today.getFullYear() - birthdate.getFullYear();
    const monthDiff = today.getMonth() - birthdate.getMonth();

    if (age < 18 || (age === 18 && monthDiff < 0)) {
      alert("Vous devez avoir au moins 18 ans.");
      this.isSubmitting = false;
      return;
    }

    const userData = {
      firstName: this.user.firstName,
      lastName: this.user.lastName,
      country: this.user.country,
      city: this.user.city,
      dob: this.formatDate(this.user.dob),
      gender: this.user.gender,
      idCardFront: this.user.idCardFront,
      idCardBack: this.user.idCardBack,
      selfie: this.user.selfie
    };

    this.http.post('http://localhost:5000/api/users', userData)
      .subscribe(
        (response) => {
          console.log('Utilisateur ajouté:', response);

          // Réinitialiser le formulaire
          this.user = {
            firstName: '',
            lastName: '',
            country: '',
            city: '',
            dob: '',
            gender: '',
            idCardFront: '',
            idCardBack: '',
            selfie: null
          };

          // Réinitialiser les inputs de fichiers
          const fileInputs = document.querySelectorAll('input[type="file"]') as NodeListOf<HTMLInputElement>;
          fileInputs.forEach(input => input.value = '');

          // ✅ Supprimer l'image de prévisualisation de la selfie
          const selfiePreview = document.getElementById('selfiePreview') as HTMLImageElement;
          if (selfiePreview) {
            selfiePreview.src = '';
          }

          // ✅ Démarrer la caméra après soumission
          this.startCamera();
        },
        (error) => {
          console.error('Erreur lors de l\'ajout:', error);
          alert('Erreur lors de l\'enregistrement: ' + error.error.message);
        }
      ).add(() => {
        this.isSubmitting = false;
      });
  }


  formatDate(date: string): string {
    const d = new Date(date);
    const day = String(d.getDate()).padStart(2, '0');
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const year = d.getFullYear();
    return `${day}/${month}/${year}`;
  }
}
