        if (comparisonResultResponse.statut !== "ok") {
          alert("Les données insérées ne se correspondent pas.");
          _this.isSubmitting = false;
          return;
        }
        // Toutes les validations sont passées - soumission des données
        const userData = {
          firstName: _this.user.firstName,
          lastName: _this.user.lastName,
          country: _this.user.country,
          city: _this.user.city,
          dob: _this.formatDate(_this.user.dob),
          dobArabic: _this.user.dobArabic,
          gender: _this.user.gender,
          idCardFront: _this.user.idCardFront,
          idCardBack: _this.user.idCardBack,
          extractedDataFront: _this.user.extractedDataFront,
          extractedDataBack: _this.user.extractedDataBack,
          selfie: _this.user.selfie,
          comparisonStatus: comparisonResultResponse.statut,
          faceVerified: verifyResponse.isIdentical,
          numeroUniqueCB: _this.user.numeroUniqueCB,
          dateDelivranceCB: _this.user.dateDelivranceCB,
          numeroUniqueExtrait: _this.user.numeroUniqueExtrait,
          Datededélivrance: _this.user.Datededélivrance
        };
        _this.http.post('http://localhost:5000/api/users', userData).subscribe(response => {
          console.log('Utilisateur ajouté avec succès!');
          alert('Création du compte se fait avec succès!');
          _this.resetForm();
        }, error => {
          console.error('Erreur lors de l\'ajout:', error);
          alert('Erreur lors de l\'enregistrement: ' + error.error.message);
        }).add(() => {
          _this.isSubmitting = false;
        });
      } catch (error) {
        console.error("Erreur:", error);
        alert("Une erreur est survenue lors de la vérification.");
        _this.isSubmitting = false;
      }
    })();
  }
  resetForm() {
    this.user = {
      firstName: '',
      lastName: '',
      email: '',
      phone: '',
      postalCode: '',
      address: '',
      country: '',
      city: '',
      dob: '',
      dobArabic: '',
      gender: '',
      genderArabic: '',
      idCardFront: '',
      idCardBack: '',
      extractedDataFront: {},
      extractedDataBack: {},
      selfie: null,
      rectoFaceId: null,
      successFaceId: null,
      numeroUniqueCB: '',
      dateDelivranceCB: '',
      numeroUniqueExtrait: '',
      Datededélivrance: ''
    };
    const fileInputs = document.querySelectorAll('input[type="file"]');
    fileInputs.forEach(input => input.value = '');
    const selfiePreview = document.getElementById('selfiePreview');
    if (selfiePreview) {
      selfiePreview.src = '';
    }
  }
  /*
  checkFocusOut(event: FocusEvent) {
    const firstname = (event.target as HTMLInputElement).value.trim();
    if (firstname !== '') {
      this.http.post('http://localhost:5000/transliterate', { text: firstname })
        .subscribe({
          next: (data: any) => console.log('Texte en arabe :', data.arabic),
          error: err => console.error('Erreur lors de la translittération:', err)
        });
    }
  }*/
  formatDate(date) {
    const d = new Date(date);
    const day = String(d.getDate()).padStart(2, '0');
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const year = d.getFullYear();
    return `${day}/${month}/${year}`;
  }
