  formatDate(date: string): string {
    const d = new Date(date);
    const day = String(d.getDate()).padStart(2, '0');
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const year = d.getFullYear();
    return `${day}/${month}/${year}`;
  }
}













<!DOCTYPE html>
<html lang="fr">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Formulaire Financier</title>
    <link href="https://fonts.googleapis.com/css2?family=Caveat:wght@400;600&display=swap" rel="stylesheet">
</head>

<body>
    <h2>Ouvrez votre avenir financier avec nous !</h2>

    <form (ngSubmit)="onSubmit()" #form="ngForm">
        <div>
            <label for="firstname">Nom</label>
            <input
            type="text"
            id="firstName"
            name="firstName"
            [(ngModel)]="user.firstName"
            (ngModelChange)="transliterateFirstName()"
            (blur)="checkFocusOutFirstName($event)"
            required
            />
        </div>
        <div>
            <label for="lastname">Prénom</label>
            <input
            type="text"
            id="lastName"
            name="lastName"
            [(ngModel)]="user.lastName"
            (ngModelChange)="transliterateLastName()"
            (blur)="checkFocusOutLastName($event)"
            required
            />
        </div>


        <div>
            <label for="country">Pays</label>
            <select [(ngModel)]="user.country" name="country" required #country="ngModel" (change)="onCountryChange()">
              <option *ngFor="let country of countries" [value]="country">{{ country }}</option>
            </select>
        </div>


        <!-- Sélection de la ville -->
        <div>
            <label for="city">Ville</label>
            <select [(ngModel)]="user.city" name="city" required #city="ngModel">
                <option *ngFor="let city of cities" [value]="city">{{ city }}</option>
            </select>
        </div>

        <div>
            <label for="dob">Date de naissance</label>
            <input type="date" [(ngModel)]="user.dob" name="dob" required #dob="ngModel" (change)="onDateChange()">
            <div *ngIf="ageError" style="color: red;">{{ ageError }}</div>
            <div *ngIf="dateError" style="color: red;">{{ dateError }}</div>
        </div>
        <div>
            <button (click)="liveness($event)">Vérifier la vivacité</button>
        </div>

        <div>
            <label for="selfie">Insérer le recto de votre pièce d'identité:</label>
            <input type="file" (change)="onFileChangeRecto($event, UserFields.IdCardFront)" />
            <div *ngIf="user.extractedDataFront">
                <strong>Résultats extractés (Recto) :</strong>
                <pre>{{ user.extractedDataFront | json }}</pre>
            </div>
        </div>

        <div>
            <label for="selfie">Insérer le verso de votre pièce d'identité:</label>
            <input type="file" (change)="onFileChangeVerso($event, UserFields.IdCardBack)" />
            <div *ngIf="user.extractedDataBack">
                <strong>Résultats extractés (Verso) :</strong>
                <pre>{{ user.extractedDataBack | json }}</pre>
            </div>
        </div>


        <div>
            <label for="gender">Genre</label>
            <select [(ngModel)]="user.gender" name="gender" required #gender="ngModel" (change)="onGenderChange(user.gender)">
                <option value="" disabled selected>Choisissez un genre</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
            </select>
        </div>

        <button (click)="onSubmit()">Soumettre</button>
    </form>
</body>
</html>
