import { HttpClient } from '@angular/common/http';
import { Component, ElementRef, ViewChild } from '@angular/core';

enum UserFields {
  FirstName = 'firstName',
  LastName = 'lastName',
  Country = 'country',
  City = 'city',
  Dob = 'dob',
  Gender = 'gender',
  IdCardFront = 'idCardFront',
  IdCardBack = 'idCardBack',
  Selfie = 'selfie'
}

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  UserFields = UserFields; // Exposer l'énumération

  @ViewChild('videoElement') videoElement!: ElementRef<HTMLVideoElement>;
  @ViewChild('canvasElement') canvasElement: ElementRef | null = null;

  user = {
    firstName: '',
    lastName: '',
    country: '',
    city: '',
    dob: '',
    gender: '',
    idCardFront: '' as string | null, // Assurez-vous que le type est correct
    idCardBack: '' as string | null,
    selfie: '' as string | null
  };

  ageError: string = '';
  dateError: string = '';

  countries: string[] = [
    "Afghanistan"
  ];

  citiesByCountry: { [key: string]: string[] } = {
    "Afghanistan": ["Kabul", "Kandahar", "Herat", "Mazar-i-Sharif", "Jalalabad"]
  };

  cities: string[] = [];

  constructor(private http: HttpClient) {}

  ngOnInit() {
    const video = document.getElementById('videoElement') as HTMLVideoElement;
    navigator.mediaDevices.getUserMedia({ video: true })
      .then(stream => {
        video.srcObject = stream;
      })
      .catch(error => {
        console.error("Erreur lors de l'accès à la caméra : ", error);
      });
  }

  onFileChange(event: Event, field: UserFields) {
    const input = event.target as HTMLInputElement;
    if (input.files && input.files.length > 0) {
      const file = input.files[0];
      const reader = new FileReader();

      reader.onload = (e) => {
        const result = e.target?.result; // Cela peut être string ou ArrayBuffer
        if (typeof result === 'string') {
          // Assurez-vous que vous assignez uniquement une chaîne
          this.user[field] = result; // Ici, vous pouvez assigner à idCardFront, idCardBack, etc.
        } else {
          // Gérer le cas où result n'est pas une chaîne
          console.error('Le type de résultat n\'est pas une chaîne.', result);
        }
      };

      reader.readAsDataURL(file); // Ou readAsText(file) selon vos besoins
    }
  }

  validateForm(): boolean {
    return (
      this.user.firstName !== '' &&
      this.user.lastName !== '' &&
      this.user.country !== '' &&
      this.user.city !== '' &&
      this.user.dob !== '' &&
      this.user.gender !== '' &&
      this.user.idCardFront !== null &&
      this.user.idCardBack !== null &&
      this.user.selfie !== null
    );
  }


  takeSelfie(event: Event) {
    // Prévenir la soumission du formulaire
    event.preventDefault();

    // Sélectionner l'élément vidéo et canvas
    const video = document.getElementById('videoElement') as HTMLVideoElement;
    const canvas = document.querySelector('canvas#canvasElement') as HTMLCanvasElement;
    const selfiePreview = document.getElementById('selfiePreview') as HTMLImageElement;

    // Vérifier que l'élément vidéo est bien présent
    if (!video) {
      console.error('Erreur : L\'élément vidéo est introuvable.');
      return;
    }

    // Créer un contexte pour le canvas
    const context = canvas.getContext('2d');
    if (!context) {
      console.error('Erreur : Impossible d\'obtenir le contexte du canvas.');
      return;
    }

    // Dessiner l'image du flux vidéo dans le canvas
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    context.drawImage(video, 0, 0, canvas.width, canvas.height);

    // Convertir le canvas en une image base64
    this.user.selfie = canvas.toDataURL('image/jpeg'); // Convertir en chaîne base64

    // Afficher l'aperçu de la selfie
    if (selfiePreview) {
      selfiePreview.src = this.user.selfie;
      selfiePreview.style.display = 'block'; // Afficher l'aperçu
    } else {
      console.error('Erreur : L\'élément d\'aperçu selfie est introuvable.');
    }

    // Arrêter le flux vidéo après la capture de la selfie
    const stream = video.srcObject as MediaStream;
    const tracks = stream?.getTracks();
    if (tracks) {
      tracks.forEach(track => track.stop()); // Arrêter tous les tracks (caméra)
    }

    // Masquer la vidéo
    video.style.display = 'none';
  }



  onCountryChange() {
    const selectedCountry = this.user.country;
    this.cities = this.citiesByCountry[selectedCountry] || [];
    this.user.city = '';
  }

  onDateChange() {
    const birthdate = new Date(this.user.dob);
    const today = new Date();

    if (birthdate.getFullYear() > today.getFullYear()) {
      this.dateError = "Choix incorrect : l'année choisie ne peut pas être supérieure à l'année actuelle.";
      this.ageError = "";
      return;
    } else {
      this.dateError = "";
    }

    const age = today.getFullYear() - birthdate.getFullYear();
    const monthDiff = today.getMonth() - birthdate.getMonth();

    if (age < 18 || (age === 18 && monthDiff < 0)) {
      this.ageError = "Vous devez avoir au moins 18 ans.";
    } else {
      this.ageError = "";
    }
  }

  onSubmit() {
    // Vérifier que la selfie est prise avant de soumettre
    if (!this.user.selfie) {
      alert("Veuillez prendre une selfie avant de soumettre.");
      return;
    }

    const birthdate = new Date(this.user.dob);
    const today = new Date();

    if (birthdate.getFullYear() > today.getFullYear()) {
      alert("Choix incorrect : l'année choisie ne peut pas être supérieure à l'année actuelle.");
      return;
    }

    const age = today.getFullYear() - birthdate.getFullYear();
    const monthDiff = today.getMonth() - birthdate.getMonth();

    if (age < 18 || (age === 18 && monthDiff < 0)) {
      alert("Vous devez avoir au moins 18 ans.");
      return;
    }

    // Collecter les données utilisateur, y compris la selfie
    const userData = {
      firstName: this.user.firstName,
      lastName: this.user.lastName,
      country: this.user.country,
      city: this.user.city,
      dob: this.formatDate(this.user.dob),
      gender: this.user.gender,
      idCardFront: this.user.idCardFront, // Doit être une chaîne, jamais null
      idCardBack: this.user.idCardBack,   // Doit être une chaîne, jamais null
      selfie: this.user.selfie             // Assurez-vous que la selfie est incluse
    };

    // Envoyer les données du formulaire au backend
    this.http.post('http://localhost:5000/api/users', userData)
      .subscribe(
        (response) => {
          console.log('Utilisateur ajouté:', response);

          // Réinitialiser le formulaire
          this.user = {
            firstName: '',
            lastName: '',
            country: '',
            city: '',
            dob: '',
            gender: '',
            idCardFront: '',  // Réinitialiser à une chaîne vide
            idCardBack: '',   // Réinitialiser à une chaîne vide
            selfie: null     // Réinitialiser la selfie à null si nécessaire
          };

          // Réinitialiser les inputs de fichiers
          const fileInputs = document.querySelectorAll('input[type="file"]') as NodeListOf<HTMLInputElement>;
          fileInputs.forEach(input => input.value = '');  // Réinitialiser tous les champs de fichier

          // Optionnel: Réinitialiser l'image prévisualisation (si elle existe)
          const previewElements = document.querySelectorAll('img[id="idCardFrontPreview"], img[id="idCardBackPreview"]') as NodeListOf<HTMLImageElement>;
          previewElements.forEach(img => img.src = '');
        },
        (error) => {
          console.error('Erreur lors de l\'ajout:', error);
          alert('Erreur lors de l\'enregistrement: ' + error.error.message);
        }
      );
      // Réinitialiser la caméra et démarrer le flux vidéo à nouveau
  this.startCamera();

  // Rafraîchir la selfie
  this.takeSelfie(new Event('submit')); // Passez l'événement de soumission pour déclencher la capture
  }

  startCamera() {
    navigator.mediaDevices.getUserMedia({ video: true })
      .then((stream) => {
        this.videoElement.nativeElement.srcObject = stream;
      })
      .catch((err) => {
        console.error('Erreur d\'accès à la caméra:', err);
      });
  }

  formatDate(date: string): string {
    const d = new Date(date);
    const day = String(d.getDate()).padStart(2, '0');
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const year = d.getFullYear();
    return `${day}/${month}/${year}`;
  }
}
<h2>Ouvrez votre avenir financier avec nous !
  <head>
    <link href="https://fonts.googleapis.com/css2?family=Caveat:wght@400;600&display=swap" rel="stylesheet">
  </head>

</h2>
<form (ngSubmit)="onSubmit()" #form="ngForm">
  <div>
    <label for="firstName">First Name:</label>
    <input [(ngModel)]="user.firstName" name="firstName" required #firstName="ngModel">
  </div>

  <div>
    <label for="lastName">Last Name:</label>
    <input [(ngModel)]="user.lastName" name="lastName" required #lastName="ngModel">
  </div>

  <div>
    <label for="country">Country:</label>
    <select [(ngModel)]="user.country" name="country" required #country="ngModel" (change)="onCountryChange()">
      <option *ngFor="let country of countries" [value]="country">{{ country }}</option>
    </select>
  </div>

  <!-- Sélection de la ville -->
  <div>
    <label for="city">City:</label>
    <select [(ngModel)]="user.city" name="city" required #city="ngModel">
      <option *ngFor="let city of cities" [value]="city">{{ city }}</option>
    </select>
  </div>

  <div>
    <label for="dob">Date of Birth:</label>
    <input type="date" [(ngModel)]="user.dob" name="dob" required #dob="ngModel" (change)="onDateChange()">
    <div *ngIf="ageError" style="color: red;">{{ ageError }}</div>
    <div *ngIf="dateError" style="color: red;">{{ dateError }}</div>
  </div>
  <div>
    <label for="selfie">Prenez une selfie:</label>
    <canvas #canvasElement style="display:none"></canvas>
    <video id="videoElement" width="640" height="420" autoplay></video>
    <video #videoElement width="640" height="420" autoplay></video>
    <canvas id="canvasElement" style="display:none"></canvas>
    <img id="selfiePreview" style="display:none" />
    <button (click)="takeSelfie($event)">Prendre une selfie</button>


  </div>

  <div>
    <label for="selfie">Insérer le recto de votre pièce d'identité:</label>
    <input type="file" (change)="onFileChange($event, UserFields.IdCardFront)" />
  </div>

  <div>
    <label for="selfie">Insérer le verso de votre pièce d'identité:</label>
    <input type="file" (change)="onFileChange($event, UserFields.IdCardBack)" />

  </div>


  <div>
    <label for="gender">Gender:</label>
    <select [(ngModel)]="user.gender" name="gender" required #gender="ngModel">
      <option value="male">Male</option>
      <option value="female">Female</option>
    </select>
  </div>
  <button (click)="onSubmit()">Submit</button>
  </form>
