import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  user = {
    firstName: '',
    lastName: '',
    country: '',
    dob: '',
    gender: ''
  };

  constructor(private http: HttpClient) {}

  onSubmit() {
    console.log("Données envoyées:", this.user);  // ✅ Debug: Vérifie les données avant l'envoi

    this.http.post('http://localhost:5000/api/users', this.user).subscribe(
      response => {
        console.log("Réponse du serveur:", response);  // ✅ Debug: Vérifie la réponse du serveur
      },
      error => {
        console.error("Erreur lors de l'envoi:", error);  // ✅ Debug: Capture les erreurs éventuelles
      }
    );
  }
}
