import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  user = {
    firstName: '',
    lastName: '',
    country: '',
    city: '',
    dob: '',
    gender: ''
  };

  countries: string[] = [
    "Afghanistan", "Albania", "Algeria", "Andorra", "Angola", "Argentina", "Armenia", "Australia",
    "Austria", "Azerbaijan", "Bahamas", "Bahrain", "Bangladesh", "Barbados", "Belarus", "Belgium",
    "Belize", "Benin", "Bhutan", "Bolivia", "Bosnia and Herzegovina", "Botswana", "Brazil", "Brunei",
    "Bulgaria", "Burkina Faso", "Burundi", "Cambodia", "Cameroon", "Canada", "Central African Republic",
    "Chad", "Chile", "China", "Colombia", "Comoros", "Congo", "Costa Rica", "Croatia", "Cuba",
    "Cyprus", "Czech Republic", "Denmark", "Djibouti", "Dominica", "Dominican Republic", "Ecuador",
    "Egypt", "El Salvador", "Equatorial Guinea", "Eritrea", "Estonia", "Eswatini", "Ethiopia", "Fiji",
    "Finland", "France", "Gabon", "Gambia", "Georgia", "Germany", "Ghana", "Greece", "Guatemala",
    "Guinea", "Haiti", "Honduras", "Hungary", "Iceland", "India", "Indonesia", "Iran", "Iraq",
    "Ireland", "Israel", "Italy", "Jamaica", "Japan", "Jordan", "Kazakhstan", "Kenya", "Kuwait",
    "Kyrgyzstan", "Laos", "Latvia", "Lebanon", "Lesotho", "Liberia", "Libya", "Liechtenstein",
    "Lithuania", "Luxembourg", "Madagascar", "Malawi", "Malaysia", "Maldives", "Mali", "Malta",
    "Mexico", "Monaco", "Mongolia", "Montenegro", "Morocco", "Mozambique", "Myanmar", "Namibia",
    "Nepal", "Netherlands", "New Zealand", "Nicaragua", "Niger", "Nigeria", "North Korea",
    "Norway", "Oman", "Pakistan", "Palestine", "Panama", "Papua New Guinea", "Paraguay", "Peru",
    "Philippines", "Poland", "Portugal", "Qatar", "Romania", "Russia", "Rwanda", "Saudi Arabia",
    "Senegal", "Serbia", "Seychelles", "Sierra Leone", "Singapore", "Slovakia", "Slovenia",
    "South Africa", "South Korea", "Spain", "Sri Lanka", "Sudan", "Suriname", "Sweden", "Switzerland",
    "Syria", "Taiwan", "Tanzania", "Thailand", "Togo", "Tunisia", "Turkey", "Uganda", "Ukraine",
    "United Arab Emirates", "United Kingdom", "United States", "Uruguay", "Uzbekistan", "Venezuela",
    "Vietnam", "Yemen", "Zambia", "Zimbabwe"
  ];

  // Liste des villes selon les pays
  citiesByCountry: { [key: string]: string[] } = {
    "Tunisia": ["Tunis", "Sfax", "Sousse", "Kairouan", "Gabès"],
    "France": ["Paris", "Marseille", "Lyon", "Toulouse", "Nice"],
    // Ajoutez d'autres pays et villes ici
  };

  cities: string[] = [];

  constructor(private http: HttpClient) {}

  // Mettre à jour la liste des villes en fonction du pays sélectionné
  onCountryChange() {
    const selectedCountry = this.user.country;
    this.cities = this.citiesByCountry[selectedCountry] || [];
    this.user.city = '';  // Réinitialiser la ville si un autre pays est sélectionné
  }

  onSubmit() {
    const userData = {
      firstName: this.user.firstName,
      lastName: this.user.lastName,
      country: this.user.country,
      city: this.user.city, // Ajouter la ville
      dob: this.formatDate(this.user.dob),  // Format the date before sending
      gender: this.user.gender
    };

    this.http.post('http://localhost:5000/api/users', userData)
      .subscribe(
        (response) => {
          console.log('Utilisateur ajouté:', response);
          this.user = {
            firstName: '',
            lastName: '',
            country: '',
            city: '',
            dob: '',
            gender: ''
          };
        },
        (error) => {
          console.error('Erreur lors de l\'ajout:', error);
          alert('Erreur lors de l\'enregistrement: ' + error.error.message);
        }
      );
  }

  // Format the date to dd/mm/yyyy
  formatDate(date: string): string {
    const d = new Date(date);
    const day = String(d.getDate()).padStart(2, '0');
    const month = String(d.getMonth() + 1).padStart(2, '0'); // months are 0-based
    const year = d.getFullYear();
    return `${day}/${month}/${year}`;
  }
}
<h2>Ouvrez votre avenir financier avec nous !
  <head>
    <link href="https://fonts.googleapis.com/css2?family=Caveat:wght@400;600&display=swap" rel="stylesheet">
  </head>

</h2>
<form (ngSubmit)="onSubmit()" #form="ngForm">
  <div>
    <label for="firstName">First Name:</label>
    <input [(ngModel)]="user.firstName" name="firstName" required #firstName="ngModel">
  </div>

  <div>
    <label for="lastName">Last Name:</label>
    <input [(ngModel)]="user.lastName" name="lastName" required #lastName="ngModel">
  </div>

  <div>
    <label for="country">Country:</label>
    <select [(ngModel)]="user.country" name="country" required #country="ngModel" (change)="onCountryChange()">
      <option *ngFor="let country of countries" [value]="country">{{ country }}</option>
    </select>
  </div>

  <!-- Sélection de la ville -->
  <div>
    <label for="city">City:</label>
    <select [(ngModel)]="user.city" name="city" required #city="ngModel">
      <option *ngFor="let city of cities" [value]="city">{{ city }}</option>
    </select>
  </div>

  <div>
    <label for="dob">Date of Birth:</label>
    <input type="date" [(ngModel)]="user.dob" name="dob" required #dob="ngModel">
  </div>

  <div>
    <label for="gender">Gender:</label>
    <select [(ngModel)]="user.gender" name="gender" required #gender="ngModel">
      <option value="male">Male</option>
      <option value="female">Female</option>
    </select>
  </div>

  <button type="submit" [disabled]="!form.valid">Submit</button>
</form>
