  async onSubmit() {
    if (this.isSubmitting) {
        return;
    }

    this.isSubmitting = true;

    // Vérifiez si la selfie a été prise
    if (!this.user.selfie) {
        alert("Veuillez prendre une selfie avant de soumettre.");
        this.isSubmitting = false;
        return;
    }

    // Vérifiez si tous les champs sont remplis
    if (!this.validateForm()) {
        alert("Veuillez remplir tous les champs du formulaire.");
        this.isSubmitting = false;
        return;
    }

    const birthdate = new Date(this.user.dob);
    const today = new Date();

    // Vérifiez la date de naissance
    if (birthdate.getFullYear() > today.getFullYear()) {
        alert("Choix incorrect : l'année choisie ne peut pas être supérieure à l'année actuelle.");
        this.isSubmitting = false;
        return;
    }

    const age = today.getFullYear() - birthdate.getFullYear();
    const monthDiff = today.getMonth() - birthdate.getMonth();

    if (age < 18 || (age === 18 && monthDiff < 0)) {
        alert("Vous devez avoir au moins 18 ans.");
        this.isSubmitting = false;
        return;
    }

    // Vérifiez que le champ "الجنس" est rempli
    if (!this.user.gender) {
        alert("Veuillez sélectionner un genre avant de soumettre.");
        this.isSubmitting = false;
        return;
    }

    console.log('Valeur du genre avant envoi:', this.user.gender);

    const translitData = {
        firstName: this.user.firstName,
        lastName: this.user.lastName,
        dob: this.user.dob,
        gender: this.user.gender
    };

    try {
        const translitResponse = await this.http.post<any>(
            'http://localhost:5000/transliterate',
            translitData,
            { headers: { 'Content-Type': 'application/json' } }
        ).toPromise();

        // Appel à l'API de comparaison
        const comparisonResponse = await this.http.post<any>(
            'http://localhost:5003/comparaison',
            {
                transliterated_data: {
                    'الاسم': translitResponse['الاسم'],
                    'اللقب': translitResponse['اللقب'],
                    'تاريخ الولادة': this.formatDate(this.user.dob), // Formatter la date de naissance
                    'الجنس': translitResponse['الجنس'],
                },
                ocr_result: this.user.extractedDataFront // ou le résultat OCR approprié
            },
            { headers: { 'Content-Type': 'application/json' } }
        ).toPromise();

        // Vérifiez le statut de la comparaison
        if (comparisonResponse && comparisonResponse.statut === "ok") {
            const userData = {
                firstName: this.user.firstName,
                lastName: this.user.lastName,
                country: this.user.country,
                city: this.user.city,
                dob: this.formatDate(this.user.dob), // Format original
                dobArabic: this.user.dobArabic, // Format arabe
                gender: this.user.gender,
                idCardFront: this.user.idCardFront,
                idCardBack: this.user.idCardBack,
                extractedDataFront: this.user.extractedDataFront,
                extractedDataBack: this.user.extractedDataBack,
                selfie: this.user.selfie,
                comparisonStatus: comparisonResponse.statut
            };

            this.http.post('http://localhost:5000/api/users', userData)
                .subscribe(
                    (response) => {
                        console.log('Utilisateur ajouté:', response);
                        // Réinitialiser le formulaire
                        this.resetForm();
                    },
                    (error) => {
                        console.error('Erreur lors de l\'ajout:', error);
                        alert('Erreur lors de l\'enregistrement: ' + error.error.message);
                    }
                ).add(() => {
                    this.isSubmitting = false;
                });
        } else {
            alert("La comparaison a échoué : " + (comparisonResponse.message || "Erreur inconnue"));
            this.isSubmitting = false;
            this.resetForm();
        }
    } catch (error: any) {
        console.error("Erreur lors de la soumission:", error);
        alert('Erreur lors de la soumission: ' + (error.error?.message || 'Erreur inconnue'));
        this.isSubmitting = false;
    }
}


resetForm() {
    this.user = {
        firstName: '',
        lastName: '',
        country: '',
        city: '',
        dob: '',
        dobArabic: '', // Ajoutez cette ligne
        gender: '',
        idCardFront: '',
        idCardBack: '',
        extractedDataFront: {},
        extractedDataBack: {},
        selfie: null
    };

    const fileInputs = document.querySelectorAll('input[type="file"]') as NodeListOf<HTMLInputElement>;
    fileInputs.forEach(input => input.value = '');

    const selfiePreview = document.getElementById('selfiePreview') as HTMLImageElement;
    if (selfiePreview) {
        selfiePreview.src = '';
    }

    this.startCamera();
}

